import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ticket.css';

const Ticket = () => {
  const { routeId } = useParams();
  const navigate = useNavigate();
  const [route, setRoute] = useState(null);
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({
    name: '',
    passport: ''
  });

  useEffect(() => {
    // Загрузка данных о маршруте
    const fetchRoute = async () => {
      try {
        const response = await fetch(`/api/routes/${routeId}`);
        const data = await response.json();
        setRoute(data);
      } catch (error) {
        console.error('Ошибка загрузки маршрута:', error);
      }
    };

    // Загрузка свободных мест
    const fetchSeats = async () => {
      try {
        const response = await fetch(`/api/routes/${routeId}/seats`);
        const data = await response.json();
        setSeats(data);
      } catch (error) {
        console.error('Ошибка загрузки мест:', error);
      }
    };

    fetchRoute();
    fetchSeats();
  }, [routeId]);

  const handleSeatSelect = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('/api/tickets/book', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          routeId,
          seatId: selectedSeat,
          passengerInfo
        })
      });

      const data = await response.json();
      
      if (response.ok) {
        navigate(`/booking-confirmation/${data.ticketId}`);
      } else {
        alert(data.message || 'Ошибка бронирования');
      }
    } catch (error) {
      console.error('Ошибка:', error);
      alert('Произошла ошибка при бронировании');
    }
  };

  if (!route) return <div className="loading">Загрузка данных...</div>;

  return (
    <div className="ticket-container">
      <h2>Оформление билета</h2>
      
      <div className="route-info">
        <h3>Информация о маршруте</h3>
        <p><strong>Отправление:</strong> {route.from} - {new Date(route.departureTime).toLocaleString()}</p>
        <p><strong>Прибытие:</strong> {route.to} - {new Date(route.arrivalTime).toLocaleString()}</p>
        <p><strong>Цена:</strong> {route.price} ₽</p>
      </div>

      <div className="seat-selection">
        <h3>Выберите место</h3>
        <div className="seats-grid">
          {seats.map(seat => (
            <button
              key={seat._id}
              className={`seat ${seat.isBooked ? 'booked' : ''} ${selectedSeat === seat._id ? 'selected' : ''}`}
              onClick={() => !seat.isBooked && handleSeatSelect(seat._id)}
              disabled={seat.isBooked}
            >
              {seat.number}
            </button>
          ))}
        </div>
      </div>

      <form onSubmit={handleSubmit} className="passenger-form">
        <h3>Данные пассажира</h3>
        
        <div className="form-group">
          <label>ФИО:</label>
          <input
            type="text"
            name="name"
            value={passengerInfo.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Паспортные данные:</label>
          <input
            type="text"
            name="passport"
            value={passengerInfo.passport}
            onChange={handleInputChange}
            required
          />
        </div>

        <button 
          type="submit" 
          className="submit-button"
          disabled={!selectedSeat}
        >
          Подтвердить бронирование
        </button>
      </form>
    </div>
  );
};

export default Ticket;
