import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const Ticket = () => {
  const { routeId } = useParams();  // –ü–æ–ª—É—á–∞–µ–º routeId –∏–∑ URL
  const navigate = useNavigate();
  const [route, setRoute] = useState(null);
  const [seats, setSeats] = useState([]);  // –≠—Ç–æ –º–æ–∂–µ—Ç –±—ã—Ç—å —Å–ø–∏—Å–æ–∫ –º–µ—Å—Ç, –∫–æ—Ç–æ—Ä—ã–µ —Ç—ã –ø–æ–ª—É—á–∏—à—å —Å —Å–µ—Ä–≤–µ—Ä–∞
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({ name: '', passport: '' });

  useEffect(() => {
  const fetchData = async () => {
    try {
      const [routesRes, seatsRes] = await Promise.all([
        fetch(`/api/routes`),              // üëà –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –í–°–ï –º–∞—Ä—à—Ä—É—Ç—ã
        fetch(`/api/routes/${routeId}/seats`) // üëà –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –º–µ—Å—Ç–∞ –∫–∞–∫ —Ä–∞–Ω—å—à–µ
      ]);

      if (!routesRes.ok || !seatsRes.ok) throw new Error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö');

      const routesData = await routesRes.json();
      const seatsData = await seatsRes.json();

      // üîç –ò—â–µ–º –Ω—É–∂–Ω—ã–π –º–∞—Ä—à—Ä—É—Ç –ø–æ ID
      const matchedRoute = routesData.find(route => route._id === routeId);

      if (!matchedRoute) {
        throw new Error('–ú–∞—Ä—à—Ä—É—Ç —Å —Ç–∞–∫–∏–º ID –Ω–µ –Ω–∞–π–¥–µ–Ω');
      }

      setRoute(matchedRoute);
      setSeats(seatsData);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:', err);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –º–∞—Ä—à—Ä—É—Ç –∏–ª–∏ –º–µ—Å—Ç–∞.');
    } finally {
      setLoading(false);
    }
  };

  fetchData();
}, [routeId]);


  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ –¥–∞–Ω–Ω—ã—Ö –±–∏–ª–µ—Ç–∞
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeat) {
      alert('–í—ã–±–µ—Ä–∏—Ç–µ –º–µ—Å—Ç–æ');
      return;
    }

    try {
      const response = await fetch('/api/routes/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          routeId,
          seatNumber: selectedSeat,
          passengerName: passengerInfo.name,
          passport: passengerInfo.passport,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        alert(data.message || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–∏');
      }
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏—è:', err);
      alert('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –¥–∞–Ω–Ω—ã—Ö');
    }
  };

  return (
    <div>
      <h2>–û—Ñ–æ—Ä–º–ª–µ–Ω–∏–µ –±–∏–ª–µ—Ç–∞</h2>
      {route && (
        <div>
          <p><strong>–û—Ç–ø—Ä–∞–≤–ª–µ–Ω–∏–µ:</strong> {route.from} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>–ü—Ä–∏–±—ã—Ç–∏–µ:</strong> {route.to} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>–¶–µ–Ω–∞:</strong> {route.price} ‚ÇΩ</p>

          <form onSubmit={handleSubmit}>
            <div>
              <label>–§–ò–û:</label>
              <input
                type="text"
                name="name"
                value={passengerInfo.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>–ü–∞—Å–ø–æ—Ä—Ç:</label>
              <input
                type="text"
                name="passport"
                value={passengerInfo.passport}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>–í—ã–±–µ—Ä–∏—Ç–µ –º–µ—Å—Ç–æ:</label>
              <select onChange={(e) => handleSeatSelect(e.target.value)} value={selectedSeat}>
                <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –º–µ—Å—Ç–æ</option>
                {seats.map((seat) => (
                  <option key={seat} value={seat}>
                    –ú–µ—Å—Ç–æ {seat}
                  </option>
                ))}
              </select>
            </div>
            <button type="submit">–ü–æ–¥—Ç–≤–µ—Ä–¥–∏—Ç—å –±—Ä–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Ticket;
