import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/profile.css';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
  });

  useEffect(() => {
    // Загружаем информацию о пользователе при монтировании компонента
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setUser(response.data);
        setFormData(response.data);  // Заполняем форму данными пользователя
      } catch (err) {
        console.error('Ошибка при загрузке профиля', err);
      }
    };

    fetchUserData();
  }, []);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put('http://localhost:5000/api/user/profile', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setUser(response.data);
      setIsEditing(false);  // Завершаем режим редактирования
    } catch (err) {
      console.error('Ошибка при сохранении данных', err);
    }
  };

  return (
    <div className="profile-container">
      <h1>Профиль пользователя</h1>
      {user ? (
        <div className="profile-details">
          <div className="profile-info">
            <div>
              <strong>Имя:</strong> {isEditing ? (
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                />
              ) : (
                <span>{user.name}</span>
              )}
            </div>
            <div>
              <strong>Электронная почта:</strong> {isEditing ? (
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                />
              ) : (
                <span>{user.email}</span>
              )}
            </div>
            <div>
              <strong>Телефон:</strong> {isEditing ? (
                <input
                  type="text"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                />
              ) : (
                <span>{user.phone}</span>
              )}
            </div>
          </div>

          <div className="profile-actions">
            {isEditing ? (
              <button onClick={handleSave}>Сохранить</button>
            ) : (
              <button onClick={() => setIsEditing(true)}>Редактировать</button>
            )}
          </div>
        </div>
      ) : (
        <p>Загрузка...</p>
      )}
    </div>
  );
};

export default Profile;
