import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../BookingConfirmation.css';

const BookingConfirmation = () => {
  const { ticketId } = useParams();
  const navigate = useNavigate();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const response = await fetch(`/api/tickets/${ticketId}`);
        
        if (!response.ok) {
          throw new Error('Билет не найден');
        }
        
        const data = await response.json();
        setTicket(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTicket();
  }, [ticketId]);

  const handlePrint = () => {
    window.print();
  };

  const handleBackToHome = () => {
    navigate('/');
  };

  if (loading) return <div className="loading">Загрузка информации о билете...</div>;
  if (error) return <div className="error">Ошибка: {error}</div>;

  return (
    <div className="confirmation-container">
      <div className="confirmation-card">
        <div className="confirmation-header">
          <h1>Бронирование подтверждено!</h1>
          <p className="confirmation-number">Номер бронирования: {ticketId}</p>
        </div>

        <div className="ticket-info">
          <div className="ticket-section">
            <h2>Информация о маршруте</h2>
            <div className="info-row">
              <span className="info-label">Направление:</span>
              <span>{ticket.route.from} → {ticket.route.to}</span>
            </div>
            <div className="info-row">
              <span className="info-label">Дата и время:</span>
              <span>{new Date(ticket.route.date).toLocaleString()}</span>
            </div>
            <div className="info-row">
              <span className="info-label">Цена:</span>
              <span className="price">{ticket.route.price} ₽</span>
            </div>
          </div>

          <div className="ticket-section">
            <h2>Информация о пассажире</h2>
            <div className="info-row">
              <span className="info-label">ФИО:</span>
              <span>{ticket.passenger.name}</span>
            </div>
            <div className="info-row">
              <span className="info-label">Паспорт:</span>
              <span>{ticket.passenger.passport}</span>
            </div>
          </div>

          <div className="ticket-section">
            <h2>Информация о месте</h2>
            <div className="info-row">
              <span className="info-label">Номер места:</span>
              <span className="seat-number">{ticket.seatNumber}</span>
            </div>
            <div className="bus-diagram">
              <div className="bus-driver">Водитель</div>
              {Array.from({ length: 12 }).map((_, row) => (
                <div key={row} className="bus-row">
                  <div 
                    className={`seat ${ticket.seatNumber === row * 4 + 1 ? 'selected' : ''}`}
                  >{row * 4 + 1}</div>
                  <div 
                    className={`seat ${ticket.seatNumber === row * 4 + 2 ? 'selected' : ''}`}
                  >{row * 4 + 2}</div>
                  <div className="aisle"></div>
                  <div 
                    className={`seat ${ticket.seatNumber === row * 4 + 3 ? 'selected' : ''}`}
                  >{row * 4 + 3}</div>
                  <div 
                    className={`seat ${ticket.seatNumber === row * 4 + 4 ? 'selected' : ''}`}
                  >{row * 4 + 4}</div>
                </div>
              ))}
              <div className="bus-door">Выход</div>
            </div>
          </div>
        </div>

        <div className="confirmation-footer">
          <p>Покажите этот билет при посадке в автобус</p>
          <div className="actions">
            <button onClick={handlePrint} className="print-btn">
              Распечатать билет
            </button>
            <button onClick={handleBackToHome} className="home-btn">
              Вернуться на главную
            </button>
          </div>
        </div>

        <div className="confirmation-qr">
          <div className="qr-code-placeholder"></div>
          <p>Или используйте QR-код для посадки</p>
        </div>
      </div>
    </div>
  );
};

export default BookingConfirmation;
