import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ticket.css';

const Ticket = () => {
  const { routeId } = useParams();
  const navigate = useNavigate();

  const [route, setRoute] = useState(null);
  const [seats, setSeats] = useState([]);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({
    name: '',
    passport: ''
  });
  const [loading, setLoading] = useState(true);

  // Загрузка маршрутов и мест
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [routesRes, seatsRes] = await Promise.all([
          fetch(`/api/routes/routes`), // Запрашиваем все маршруты
          fetch(`/api/routes/${routeId}/seats`) // Запрашиваем места
        ]);

        if (!routesRes.ok || !seatsRes.ok) throw new Error('Ошибка загрузки данных');

        const routesData = await routesRes.json();
        const seatsData = await seatsRes.json();

        // Ищем нужный маршрут по routeId
        const matchedRoute = routesData.find(route => route._id === routeId);
        if (!matchedRoute) throw new Error('Маршрут не найден');

        setRoute(matchedRoute);
        setSeats(seatsData);
      } catch (err) {
        console.error('Ошибка при получении данных:', err);
        alert('Не удалось загрузить маршрут или места.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [routeId]);

  const handleSeatSelect = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeat) {
      alert('Выберите место');
      return;
    }

    try {
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          routeId,
          seatNumber: selectedSeat, // Изменено на seatNumber для соответствия серверу
          passengerName: passengerInfo.name,
          email: 'testt@mail.ru' // Замените или добавьте поле email в форму, если нужно
        })
      });

      const data = await response.json();

      if (response.ok) {
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        alert(data.message || 'Ошибка при бронировании');
      }
    } catch (err) {
      console.error('Ошибка бронирования:', err);
      alert('Произошла ошибка при отправке данных');
    }
  };

  if (loading) return <div className="loading">Загрузка маршрута...</div>;
  if (!route) return <div className="error">Маршрут не найден</div>;

  return (
    <div className="ticket-container">
      <h2>Оформление билета</h2>

      <div className="route-info">
        <h3>Информация о маршруте</h3>
        <p><strong>Отправление:</strong> {route.from} - {new Date(route.date).toLocaleString()}</p>
        <p><strong>Прибытие:</strong> {route.to}</p>
        <p><strong>Цена:</strong> {route.price} ₽</p>
      </div>

      <div className="seat-selection">
        <h3>Выберите место</h3>
        <div className="seats-grid">
          {seats.map(seat => (
            <button
              key={seat._id}
              className={`seat ${seat.isBooked ? 'booked' : ''} ${selectedSeat === seat._id ? 'selected' : ''}`}
              onClick={() => !seat.isBooked && handleSeatSelect(seat._id)}
              disabled={seat.isBooked}
            >
              {seat.number}
            </button>
          ))}
        </div>
      </div>

      <form onSubmit={handleSubmit} className="passenger-form">
        <h3>Данные пассажира</h3>

        <div className="form-group">
          <label>ФИО:</label>
          <input
            type="text"
            name="name"
            value={passengerInfo.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Паспорт:</label>
          <input
            type="text"
            name="passport"
            value={passengerInfo.passport}
            onChange={handleInputChange}
            required
          />
        </div>

        <button type="submit" className="submit-button" disabled={!selectedSeat}>
          Подтвердить бронирование
        </button>
      </form>
    </div>
  );
};

export default Ticket;
