// src/components/SearchForm.js
import { useState, useEffect } from 'react';
import '../styles/searchForm.css';
import { getCities } from '../api/getCities';

export default function SearchForm({ onSearch }) {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  const [showFromList, setShowFromList] = useState(false);
  const [showToList, setShowToList] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ from, to, date });
  };

  const handleCityInput = async (value, setValue, setSuggestions, setShowList) => {
    setValue(value);
    const suggestions = await getCities(value);
    setSuggestions(suggestions);
    setShowList(true);
  };

  return (
    <form onSubmit={handleSubmit} className="search-form">
      <div className="input-wrapper">
        <input
          value={from}
          onChange={(e) => handleCityInput(e.target.value, setFrom, setFromSuggestions, setShowFromList)}
          placeholder="Откуда"
          onFocus={() => setShowFromList(true)}
          onBlur={() => setTimeout(() => setShowFromList(false), 200)}
          required
        />
        {showFromList && fromSuggestions.length > 0 && (
          <ul className="suggestions up">
            {fromSuggestions.map((city, index) => (
              <li key={index} onClick={() => { setFrom(city); setShowFromList(false); }}>{city}</li>
            ))}
          </ul>
        )}
      </div>

      <div className="input-wrapper">
        <input
          value={to}
          onChange={(e) => handleCityInput(e.target.value, setTo, setToSuggestions, setShowToList)}
          placeholder="Куда"
          onFocus={() => setShowToList(true)}
          onBlur={() => setTimeout(() => setShowToList(false), 200)}
          required
        />
        {showToList && toSuggestions.length > 0 && (
          <ul className="suggestions up">
            {toSuggestions.map((city, index) => (
              <li key={index} onClick={() => { setTo(city); setShowToList(false); }}>{city}</li>
            ))}
          </ul>
        )}
      </div>

      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
      <button type="submit">Найти</button>
    </form>
  );
}
