import { useNavigate } from 'react-router-dom';
import '../styles/routeList.css';

const RoutesList = () => {
  const [routes, setRoutes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRoutes = async () => {
      try {
        const res = await fetch('/api/routes/routes');
        const data = await res.json();
        setRoutes(data);
      } catch (err) {
        console.error('Ошибка при получении маршрутов:', err);
      }
    };

    fetchRoutes();
  }, []);

  return (
    <div className="route-list-container">
      <h2>Найденные маршруты</h2>
      <table className="route-table">
        <thead>
          <tr>
            <th>Отправление</th>
            <th>Прибытие</th>
            <th>Время в пути</th>
            <th>Тип транспорта</th>
            <th>Цена</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {routes.map(route => (
            <tr key={route._id}>
              <td data-label="Отправление">
                <div className="time-cell">
                  <span className="time">
                    {new Date(route.departureTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                  </span>
                  <span className="city">{route.from}</span>
                </div>
              </td>
              <td data-label="Прибытие">
                <div className="time-cell">
                  <span className="time">
                    {new Date(route.arrivalTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                  </span>
                  <span className="city">{route.to}</span>
                </div>
              </td>
              <td data-label="Время в пути">
                {Math.floor(route.duration / 60)}ч {route.duration % 60}мин
              </td>
              <td data-label="Тип транспорта">
                <span className="transport-badge">{route.transportType}</span>
              </td>
              <td data-label="Цена">
                <span className="price">{route.price} ₽</span>
              </td>
              <td>
                <button 
                  className="book-button"
                  onClick={() => handleBookClick(route._id)}
                >
                  Выбрать
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RouteList;
