import { useState } from 'react';
import '../styles/searchForm.css';

const cities = [
  'Москва', 'Санкт-Петербург', 'Казань', 'Новосибирск',
  'Екатеринбург', 'Краснодар', 'Сочи', 'Нижний Новгород', 'Стерлитамак'
];

export default function SearchForm({ onSearch }) {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [showFromSuggestions, setShowFromSuggestions] = useState(false);
  const [showToSuggestions, setShowToSuggestions] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ from, to, date });
  };

  const filterCities = (input) =>
    cities.filter((city) =>
      city.toLowerCase().startsWith(input.toLowerCase())
    );

  return (
    <form onSubmit={handleSubmit} className="search-form">
      <div className="input-group">
        <input
          value={from}
          onChange={(e) => setFrom(e.target.value)}
          onFocus={() => setShowFromSuggestions(true)}
          onBlur={() => setTimeout(() => setShowFromSuggestions(false), 150)}
          placeholder="Откуда"
        />
        {showFromSuggestions && from && (
          <ul className="suggestions">
            {filterCities(from).map((city) => (
              <li key={city} onClick={() => setFrom(city)}>
                {city}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="input-group">
        <input
          value={to}
          onChange={(e) => setTo(e.target.value)}
          onFocus={() => setShowToSuggestions(true)}
          onBlur={() => setTimeout(() => setShowToSuggestions(false), 150)}
          placeholder="Куда"
        />
        {showToSuggestions && to && (
          <ul className="suggestions">
            {filterCities(to).map((city) => (
              <li key={city} onClick={() => setTo(city)}>
                {city}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="input-group">
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
      </div>

      <button type="submit">Найти</button>
    </form>
  );
}
