import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const Ticket = () => {
  const { routeId } = useParams(); // Получаем routeId из URL
  const navigate = useNavigate();
  const [route, setRoute] = useState(null); // Состояние для маршрута
  const [seats, setSeats] = useState([]); // Состояние для мест
  const [selectedSeat, setSelectedSeat] = useState(null); // Состояние для выбранного места
  const [passengerInfo, setPassengerInfo] = useState({ name: '', passport: '' }); // Состояние для данных пассажира

  // Загрузка данных маршрута и мест
  useEffect(() => {
    const fetchRouteDetails = async () => {
      try {
        const routeResponse = await fetch(`/api/routes/${routeId}`);
        if (!routeResponse.ok) {
          throw new Error('Маршрут не найден');
        }
        const routeData = await routeResponse.json();
        setRoute(routeData); // Сохраняем данные маршрута

        // Загрузка доступных мест (например, с сервера или генерация)
        const seatsResponse = await fetch(`/api/seats/${routeId}`);
        if (!seatsResponse.ok) {
          throw new Error('Не удалось загрузить места');
        }
        const seatsData = await seatsResponse.json();
        setSeats(seatsData); // Сохраняем доступные места
      } catch (error) {
        console.error('Ошибка при получении данных:', error);
        alert('Ошибка при загрузке данных. Попробуйте позже.');
      }
    };

    fetchRouteDetails();
  }, [routeId]);

  // Обработка выбора места
  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat); // Сохраняем выбранное место
  };

  // Обработка изменения данных пассажира
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Обработка отправки данных билета
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeat) {
      alert('Пожалуйста, выберите место');
      return;
    }

    try {
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          routeId,
          seatNumber: selectedSeat,
          passengerName: passengerInfo.name,
          passport: passengerInfo.passport,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Перенаправляем на страницу подтверждения после успешного бронирования
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        alert(data.message || 'Ошибка при бронировании');
      }
    } catch (err) {
      console.error('Ошибка при бронировании:', err);
      alert('Произошла ошибка при отправке данных');
    }
  };

  return (
    <div>
      <h2>Оформление билета</h2>
      {route ? (
        <div>
          <p><strong>Отправление:</strong> {route.from} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>Прибытие:</strong> {route.to} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>Цена:</strong> {route.price} ₽</p>

          <form onSubmit={handleSubmit}>
            <div>
              <label>ФИО:</label>
              <input
                type="text"
                name="name"
                value={passengerInfo.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Паспорт:</label>
              <input
                type="text"
                name="passport"
                value={passengerInfo.passport}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Выберите место:</label>
              <select onChange={(e) => handleSeatSelect(e.target.value)} value={selectedSeat}>
                <option value="">Выберите место</option>
                {seats.length > 0 ? (
                  seats.map((seat) => (
                    <option key={seat} value={seat}>
                      Место {seat}
                    </option>
                  ))
                ) : (
                  <option disabled>Места недоступны</option>
                )}
              </select>
            </div>
            <button type="submit">Подтвердить бронирование</button>
          </form>
        </div>
      ) : (
        <p>Загрузка данных маршрута...</p>
      )}
    </div>
  );
};

export default Ticket;
