import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/admin.css';
import axios from 'axios';

export default function Index() {
  const [loading, setLoading] = useState(true);
  const [routes, setRoutes] = useState([]);
  const [error, setError] = useState('');
  const [newRoute, setNewRoute] = useState({
    from: '',
    to: '',
    date: '',
    price: '',
    busNumber: '',
    duration: '',
    departureTime: '',
    arrivalTime: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/admin/login');
    } else {
      setLoading(false);
      fetchRoutes(token);
    }
  }, [navigate]);

  const fetchRoutes = async (token) => {
    try {
      const response = await axios.get('http://localhost:5000/api/routes', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setRoutes(response.data);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка загрузки маршрутов');
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/admin/login');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/admin/login');
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5000/api/routes/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchRoutes(token);
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка при удалении');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewRoute({ ...newRoute, [name]: value });
  };

  const calculateArrivalTime = (departureTime, duration) => {
    if (!departureTime || !duration) return '';
    
    const [hours, minutes] = departureTime.split(':').map(Number);
    const durationInMinutes = duration * 60;
    const totalMinutes = hours * 60 + minutes + durationInMinutes;
    
    const arrivalHours = Math.floor(totalMinutes / 60) % 24;
    const arrivalMinutes = totalMinutes % 60;
    
    return `${String(arrivalHours).padStart(2, '0')}:${String(arrivalMinutes).padStart(2, '0')}`;
  };

  const handleAddRoute = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const arrivalTime = calculateArrivalTime(newRoute.departureTime, newRoute.duration);

    try {
      await axios.post(
        'http://localhost:5000/api/routes',
        { 
          ...newRoute, 
          arrivalTime,
          date: newRoute.date // Убедитесь, что дата в правильном формате
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchRoutes(token);
      setNewRoute({
        from: '',
        to: '',
        date: '',
        price: '',
        busNumber: '',
        duration: '',
        departureTime: '',
        arrivalTime: ''
      });
    } catch (err) {
      setError(err.response?.data?.message || 'Ошибка при добавлении маршрута');
    }
  };

  const formatTime = (timeStr) => {
    if (!timeStr) return '';
    // Если время уже в формате HH:mm
    if (/^\d{2}:\d{2}$/.test(timeStr)) {
      return timeStr;
    }
    // Если это ISO строка
    try {
      const date = new Date(timeStr);
      if (!isNaN(date)) {
        return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: false });
      }
    } catch (e) {
      return timeStr;
    }
    return timeStr;
  };

  if (loading) return <div className="loading">Загрузка...</div>;

  return (
    <div className="admin-container">
      <header className="admin-header">
        <h1>Панель администратора</h1>
        <button onClick={handleLogout} className="logout-btn">Выйти</button>
      </header>
      
      <div className="add-route">
        <h2>Добавить новый маршрут</h2>
        <form onSubmit={handleAddRoute} className="add-route-form">
          <input type="text" name="from" value={newRoute.from} onChange={handleChange} placeholder="Откуда" required />
          <input type="text" name="to" value={newRoute.to} onChange={handleChange} placeholder="Куда" required />
          <input type="date" name="date" value={newRoute.date} onChange={handleChange} required />
          <input type="text" name="departureTime" value={newRoute.departureTime} onChange={handleChange} required placeholder="Время отправления" />
          <input type="number" name="price" value={newRoute.price} onChange={handleChange} placeholder="Цена" required />
          <input type="text" name="busNumber" value={newRoute.busNumber} onChange={handleChange} placeholder="Номер автобуса" required />
          <input type="number" step="0.5" name="duration" value={newRoute.duration} onChange={handleChange} placeholder="Длительность (часы)" required />
          <button type="submit" className="add-btn">Добавить маршрут</button>
        </form>
        {error && <div className="error-message">{error}</div>}
      </div>
      
      <div className="routes-list">
        <h2>Список маршрутов</h2>
        {routes.length > 0 ? (
          <table className="routes-table">
            <thead>
              <tr>
                <th>Откуда</th>
                <th>Куда</th>
                <th>Дата</th>
                <th>Отправление</th>
                <th>Прибытие</th>
                <th>Цена</th>
                <th>Автобус</th>
                <th>Длительность</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {routes.map((route) => (
                <tr key={route._id}>
                  <td>{route.from}</td>
                  <td>{route.to}</td>
                  <td>{new Date(route.date).toLocaleDateString()}</td>
                  <td>{route.departureTime.toLocaleTimeString()}</td>
                  <td>{formatTime(route.arrivalTime)}</td>
                  <td>{route.price} ₽</td>
                  <td>{route.busNumber}</td>
                  <td>{route.duration} ч</td>
                  <td>
                    <button onClick={() => handleDelete(route._id)} className="delete-btn">
                      Удалить
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Нет доступных маршрутов</p>
        )}
      </div>
    </div>
  );
}
