// RouteList.js
import React from 'react';
import '../styles/routeList.css';

const RouteList = ({ routes }) => {
  if (routes.length === 0) {
    return (
      <div className="route-list-container">
        <div className="route-empty">Маршруты не найдены. Попробуйте изменить параметры поиска.</div>
      </div>
    );
  }

  return (
    <div className="route-list-container">
      <h2>Найденные маршруты</h2>
      <div className="route-grid">
        {routes.map(route => (
          <div key={route._id} className="route-card">
            <div className="route-info">
              <div className="route-time">
                <span className="time">{new Date(route.departureTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
                <span className="city">{route.from}</span>
              </div>
              
              <div className="route-duration">
                <div className="duration-line"></div>
                <span className="duration-text">{Math.floor(route.duration / 60)}ч {route.duration % 60}мин</span>
              </div>
              
              <div className="route-time">
                <span className="time">{new Date(route.arrivalTime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</span>
                <span className="city">{route.to}</span>
              </div>
            </div>
            
            <div className="route-meta">
              <div className="route-price">{route.price} ₽</div>
              <div className="route-transport">{route.transportType}</div>
              <button className="route-book">Выбрать</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RouteList;
