// ConfirmEmail.jsx
import { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';

const ConfirmEmail = () => {
  const [searchParams] = useSearchParams();
  const [message, setMessage] = useState('');
  const token = searchParams.get('token');

  useEffect(() => {
    const confirmEmail = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/auth/confirm-email?token=${token}`);
        const data = await res.json();
        if (!res.ok) throw new Error(data.message || 'Ошибка подтверждения');
        setMessage('Email успешно подтверждён!');
      } catch (err) {
        setMessage(err.message);
      }
    };

    if (token) confirmEmail();
  }, [token]);

  return (
    <div style={{ padding: '2rem', textAlign: 'center' }}>
      <h1>Подтверждение email</h1>
      <p>{message || 'Подтверждаем...'}</p>
    </div>
  );
};

export default ConfirmEmail;
