import { useState } from 'react';
import '../styles/searchForm.css'; // Подключаем стили
import axios from 'axios';

export default function SearchForm({ onSearch }) {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Создаем объект поиска с только теми параметрами, которые были заполнены
    const searchParams = {};
    if (from) searchParams.from = from;
    if (to) searchParams.to = to;
    if (date) searchParams.date = date;

    onSearch(searchParams); // Отправляем параметры поиска
  };

  return (
    <form onSubmit={handleSubmit} className="search-form">
      <input
        value={from}
        onChange={(e) => setFrom(e.target.value)}
        placeholder="Откуда"
      />
      <input
        value={to}
        onChange={(e) => setTo(e.target.value)}
        placeholder="Куда"
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <button type="submit">Найти</button>
    </form>
  );
}
