import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const Ticket = () => {
  const { routeId } = useParams();  // Получаем routeId из URL
  const navigate = useNavigate();
  const [route, setRoute] = useState(null);
  const [seats, setSeats] = useState([]);  // Это может быть список мест, которые ты получишь с сервера
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({ name: '', passport: '' });

  useEffect(() => {
  const fetchRouteDetails = async () => {
    try {
      const response = await fetch(`/api/routes/${routeId}`);
      
      // Проверяем статус ответа
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP ${response.status}: ${errorText}`);
      }
      
      // Проверяем Content-Type
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        const text = await response.text();
        throw new Error(`Expected JSON, got: ${text.substring(0, 100)}...`);
      }
      
      const routeData = await response.json();
      setRoute(routeData);
    } catch (error) {
      console.error('Ошибка при получении маршрута:', error);
      alert(`Ошибка загрузки маршрута: ${error.message}`);
    }
  };

  fetchRouteDetails();
}, [routeId]);

  // Обработка выбора места
  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat);
  };

  // Обработка изменения данных пассажира
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Обработка отправки данных билета
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeat) {
      alert('Выберите место');
      return;
    }

    try {
      const response = await fetch('/api/routes/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          routeId,
          seatNumber: selectedSeat,
          passengerName: passengerInfo.name,
          passport: passengerInfo.passport,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        alert(data.message || 'Ошибка при бронировании');
      }
    } catch (err) {
      console.error('Ошибка бронирования:', err);
      alert('Произошла ошибка при отправке данных');
    }
  };

  return (
    <div>
      <h2>Оформление билета</h2>
      {route && (
        <div>
          <p><strong>Отправление:</strong> {route.from} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>Прибытие:</strong> {route.to} - {new Date(route.date).toLocaleString()}</p>
          <p><strong>Цена:</strong> {route.price} ₽</p>

          <form onSubmit={handleSubmit}>
            <div>
              <label>ФИО:</label>
              <input
                type="text"
                name="name"
                value={passengerInfo.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Паспорт:</label>
              <input
                type="text"
                name="passport"
                value={passengerInfo.passport}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Выберите место:</label>
              <select onChange={(e) => handleSeatSelect(e.target.value)} value={selectedSeat}>
                <option value="">Выберите место</option>
                {seats.map((seat) => (
                  <option key={seat} value={seat}>
                    Место {seat}
                  </option>
                ))}
              </select>
            </div>
            <button type="submit">Подтвердить бронирование</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Ticket;
