import React, { useState, useContext, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import '../styles/Auth.css';

const Login = ({ onClose, onSwitchToRegister }) => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { setUser, setIsAuth } = useContext(AuthContext);

  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  useEffect(() => {
    const success = searchParams.get('success');
    const error = searchParams.get('error');

    if (success === 'email_confirmed') {
      setSuccessMessage('✅ Email успешно подтвержден! Теперь войдите.');
    }

    if (error === 'invalid_or_used') {
      setError('❌ Неверный или уже использованный токен');
    }

    if (error === 'already_confirmed') {
      setError('⚠️ Email уже был подтвержден ранее');
    }

    if (error === 'missing_token') {
      setError('❌ Токен не передан');
    }

    if (error === 'server_error') {
      setError('⚠️ Ошибка сервера при подтверждении');
    }

    // Удаляем параметры после отображения
    if (success || error) {
      setTimeout(() => {
        searchParams.delete('success');
        searchParams.delete('error');
        setSearchParams(searchParams);
      }, 5000); // ⏳ Очистка через 5 сек
    }
  }, [searchParams, setSearchParams]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Ошибка входа');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);

      setUser({
        email: formData.email,
        token: data.token,
        userId: data.userId,
      });

      setIsAuth(true);
      onClose();
    } catch (err) {
      console.error('❌ Ошибка логина:', err.message);
      setError(err.message || 'Ошибка входа');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-modal-overlay">
      <div className="auth-modal">
        <button className="auth-close-btn" onClick={onClose}>&times;</button>
        <h2 className="auth-title">Вход в аккаунт</h2>

        {successMessage && <div className="auth-success">{successMessage}</div>}
        {error && <div className="auth-error">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="auth-input-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Ваш email"
            />
          </div>
          <div className="auth-input-group">
            <label>Пароль</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Ваш пароль"
            />
          </div>
          <button type="submit" className="auth-submit-btn" disabled={isLoading}>
            {isLoading ? 'Вход...' : 'Войти'}
          </button>
        </form>
        <div className="auth-switch">
          Нет аккаунта?{' '}
          <button className="auth-switch-btn" onClick={onSwitchToRegister}>
            Зарегистрироваться
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
