import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaBus, FaCalendarAlt, FaUser, FaMoneyBillWave, FaArrowLeft, FaPrint, FaTimes, FaCheck } from 'react-icons/fa';
import '../styles/TicketDetails.css';

const TicketDetails = () => {
  const { id } = useParams();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isCancelling, setIsCancelling] = useState(false);
  const [cancelSuccess, setCancelSuccess] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTicket = async () => {
      const token = localStorage.getItem('token');
      try {
        const res = await fetch(`http://localhost:5000/api/tickets/id/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!res.ok) {
          throw new Error(res.status === 404 ? 'Билет не найден' : 'Ошибка при загрузке билета');
        }

        const data = await res.json();
        setTicket(data);
      } catch (err) {
        setError(err.message.includes('Failed to fetch') 
          ? 'Ошибка соединения с сервером' 
          : err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTicket();
  }, [id]);

  const handlePrint = () => {
    window.print();
  };

  const handleCancelTicket = async () => {
    if (!window.confirm('Вы уверены, что хотите отменить этот билет?')) {
      return;
    }

    setIsCancelling(true);
    setError(null);
    const token = localStorage.getItem('token');
    try {
      const res = await fetch(`http://localhost:5000/api/tickets/${id}/cancel`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!res.ok) {
        throw new Error(res.status === 404 ? 'Билет не найден' : 'Ошибка при отмене билета');
      }

      const updatedTicket = await res.json();
      setTicket(updatedTicket);
      setCancelSuccess(true);
      setTimeout(() => setCancelSuccess(false), 3000);
    } catch (err) {
      setError(err.message.includes('Failed to fetch') 
        ? 'Не удалось соединиться с сервером. Проверьте интернет-соединение.' 
        : err.message);
    } finally {
      setIsCancelling(false);
    }
  };

  if (loading) return (
    <div className="loading-container">
      <div className="loading-spinner"></div>
      <p>Загрузка билета...</p>
    </div>
  );

  if (error) return (
    <div className="error-container">
      <h2>Ошибка</h2>
      <p>{error}</p>
      <div className="action-buttons">
        <button className="primary-btn" onClick={() => window.location.reload()}>
          Попробовать снова
        </button>
        <button className="secondary-btn" onClick={() => navigate(-1)}>
          Вернуться назад
        </button>
      </div>
    </div>
  );

  if (!ticket) return null;

  return (
    <div className="ticket-details">
      <div className="ticket-header">
        <button onClick={() => navigate(-1)} className="back-btn">
          <FaArrowLeft /> Назад
        </button>
        <h2><FaBus /> Билет #{ticket._id.slice(-6).toUpperCase()}</h2>
      </div>

      {cancelSuccess && (
        <div className="success-message">
          <FaCheck /> Билет успешно отменен!
        </div>
      )}

      <div className={`ticket-card ${ticket.status}`}>
        {/* ... остальное содержимое ticket-card ... */}
      </div>

      <div className="ticket-actions">
        <button onClick={handlePrint} className="print-btn">
          <FaPrint /> Распечатать
        </button>
        
        {ticket.status === 'booked' && (
          <button 
            onClick={handleCancelTicket} 
            className="cancel-btn"
            disabled={isCancelling}
          >
            {isCancelling ? (
              <>
                <span className="spinner"></span> Отмена...
              </>
            ) : (
              <>
                <FaTimes /> Отменить билет
              </>
            )}
          </button>
        )}
      </div>
    </div>
  );
};

export default TicketDetails;
