import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="header">
      <div className="header-container">
        <Link to="/" className="logo-link">
          <span className="logo-text">GoByBus</span>
        </Link>

        <button 
          className="menu-toggle"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          aria-label="Toggle menu"
        >
          <div className={`hamburger ${isMenuOpen ? 'open' : ''}`} />
        </button>

        <nav className={`nav ${isMenuOpen ? 'open' : ''}`}>
          <div className="nav-links">
            <NavLink to="/" text="Главная" />
            <NavLink to="/tours" text="Путевки" />
            <NavLink to="/support" text="Поддержка" />
          </div>
          <Link to="/auth" className="auth-button">Вход</Link>
        </nav>
      </div>
    </header>
  );
};

const NavLink = ({ to, text }) => (
  <Link 
    to={to} 
    className="nav-link"
    activeClassName="active"
  >
    <span className="link-hover-effect">{text}</span>
  </Link>
);

export default Header;
