// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchForm from './components/SearchForm';
import RouteList from './components/RouteList';

function App() {
  const [routes, setRoutes] = useState([]);
  const [filteredRoutes, setFilteredRoutes] = useState([]);

  useEffect(() => {
    // Загружаем все маршруты при старте
    axios.get('http://localhost:5000/api/routes')
      .then(response => setRoutes(response.data))
      .catch(error => console.error('Ошибка загрузки маршрутов:', error));
  }, []);

  const handleSearch = (searchParams) => {
    const { from, to, date } = searchParams;

    const filtered = routes.filter(route =>
      route.departureCity.toLowerCase().includes(from.toLowerCase()) &&
      route.arrivalCity.toLowerCase().includes(to.toLowerCase())
      // Здесь можно добавить проверку по дате, если маршрут имеет дату
    );

    setFilteredRoutes(filtered);
  };

  return (
    <div>
      <SearchForm onSearch={handleSearch} />
      <RouteList routes={filteredRoutes} />
    </div>
  );
}

export default App;
