        <SearchForm onSearch={handleSearch} />
      <RouteList routes={filteredRoutes} />
      </div>

      <div className="features-section">
        <div className="feature-card">
          <div className="feature-icon">‚úàÔ∏è</div>
          <h3>–ë—ã—Å—Ç—Ä–æ</h3>
