import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchForm from '../components/SearchForm';
import '../styles/home.css';
import RouteList from '../components/RouteList';

const Home = () => {
  const [routes, setRoutes] = useState([]);          // Все маршруты с сервера
  const [filteredRoutes, setFilteredRoutes] = useState([]); // Фильтрованные маршруты после поиска

  useEffect(() => {
    // Загружаем маршруты один раз при загрузке страницы
    axios.get('http://localhost:5000/api/routes/routes')
      .then(response => {
        setRoutes(response.data);
        setFilteredRoutes(response.data);  // Изначально показываем все маршруты
      })
      .catch(error => console.error('Ошибка загрузки маршрутов:', error));
  }, []);

  const handleSearch = (searchParams) => {
    const { from, to, date } = searchParams;

    const filtered = routes.filter(route => {
      // Фильтрация по "Откуда"
      const matchesFrom = from
        ? route.from?.toLowerCase().includes(from.toLowerCase())
        : true;

      // Фильтрация по "Куда"
      const matchesTo = to
        ? route.to?.toLowerCase().includes(to.toLowerCase())
        : true;

      // Фильтрация по дате
      const matchesDate = date
        ? new Date(route.date).toLocaleDateString() === new Date(date).toLocaleDateString()
        : true;

      return matchesFrom && matchesTo && matchesDate;
    });

    setFilteredRoutes(filtered);
  };


  return (
    <div className="home-page">
      <div className="hero-section">
        <h1>Найдите идеальное путешествие</h1>
        <p className="subtitle">Откройте для себя лучшие направления по выгодным ценам</p>

        <SearchForm onSearch={handleSearch} />
      <RouteList routes={filteredRoutes} />
      </div>

      <div className="features-wrapper">
  <h2 className="features-title">Почему выбирают нас</h2>
  <div className="features-grid">
    <div className="feature-item">
      <div className="feature-icon">⚡</div>
      <div>
        <h3>Моментальный поиск</h3>
        <p>Получайте результаты за доли секунды, без лишнего ожидания.</p>
      </div>
    </div>
    <div className="feature-item">
      <div className="feature-icon">🛡️</div>
      <div>
        <h3>Надёжные перевозчики</h3>
        <p>Сотрудничаем только с проверенными компаниями.</p>
      </div>
    </div>
    <div className="feature-item">
      <div className="feature-icon">🎯</div>
      <div>
        <h3>Прозрачные цены</h3>
        <p>Без скрытых комиссий и навязанных услуг.</p>
      </div>
    </div>
    <div className="feature-item">
      <div className="feature-icon">📱</div>
      <div>
        <h3>Удобно с мобильного</h3>
        <p>Адаптивный интерфейс — ищите билеты где угодно.</p>
      </div>
    </div>
  </div>
</div>

    </div>
  );
};

export default Home;
