import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ticket.css';

const Ticket = () => {
  const { routeId } = useParams();
  const navigate = useNavigate();
  const [route, setRoute] = useState(null);
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({ 
    name: '', 
    passport: '' 
  });
  const [isLoading, setIsLoading] = useState(true);
  const [busLayout, setBusLayout] = useState([]);

  // Генерация схемы автобуса (48 мест, 12 рядов)
  useEffect(() => {
    const generateBusLayout = () => {
      const layout = [];
      let seatId = 1;
      
      for (let row = 0; row < 12; row++) {
        const rowSeats = [];
        // Левая сторона (места 1-2)
        rowSeats.push(seatId++);
        rowSeats.push(seatId++);
        // Проход (null)
        rowSeats.push(null);
        // Правая сторона (места 3-4)
        rowSeats.push(seatId++);
        rowSeats.push(seatId++);
        
        layout.push(rowSeats);
      }
      
      setBusLayout(layout);
    };

    generateBusLayout();
  }, []);

  useEffect(() => {
    const fetchRouteDetails = async () => {
      try {
        setIsLoading(true);
        const response = await fetch(`http://localhost:5000/api/routes/${routeId}`);
        
        if (!response.ok) {
          throw new Error('Маршрут не найден');
        }
        
        const data = await response.json();
        setRoute(data);
      } catch (err) {
        console.error('Ошибка:', err);
      } finally {
        setIsLoading(false);
      }
    };

    if (routeId && typeof routeId === 'string') {
      fetchRouteDetails();
    } else {
      console.error('Invalid routeId:', routeId);
    }
  }, [routeId]);

  const handleSeatClick = (seatId) => {
    setSelectedSeat(seatId);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedSeat) {
      alert('Пожалуйста, выберите место');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/tickets/book', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json' 
        },
        body: JSON.stringify({
          routeId,
          seatId: selectedSeat,
          passengerName: passengerInfo.name,
          passport: passengerInfo.passport,
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        throw new Error(data.message || 'Ошибка бронирования');
      }
    } catch (err) {
      alert(err.message);
      console.error('Ошибка бронирования:', err);
    }
  };

  if (isLoading) return <div className="loading">Загрузка данных...</div>;
  if (!route) return <div className="error">Маршрут не найден</div>;

  return (
    <div className="ticket-booking">
      <header className="booking-header">
        <h1>Оформление билета</h1>
        <div className="route-badge">№ {routeId}</div>
      </header>

      <section className="route-details">
        <div className="route-info">
          <div className="info-block">
            <span className="info-label">Направление</span>
            <h2>{route.from} → {route.to}</h2>
          </div>
          
          <div className="info-grid">
            <div className="info-item">
              <span className="info-label">Дата отправления</span>
              <p>{new Date(route.date).toLocaleDateString()}</p>
            </div>
            <div className="info-item">
              <span className="info-label">Время</span>
              <p>{new Date(route.date).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</p>
            </div>
            <div className="info-item">
              <span className="info-label">Цена</span>
              <p className="price">{route.price} ₽</p>
            </div>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-section">
            <h3>Данные пассажира</h3>
            
            <div className="form-group">
              <label htmlFor="name">ФИО</label>
              <input
                id="name"
                type="text"
                name="name"
                value={passengerInfo.name}
                onChange={handleInputChange}
                required
                placeholder="Иванов Иван Иванович"
              />
            </div>

            <div className="form-group">
              <label htmlFor="passport">Паспортные данные</label>
              <input
                id="passport"
                type="text"
                name="passport"
                value={passengerInfo.passport}
                onChange={handleInputChange}
                required
                placeholder="1234 567890"
              />
            </div>
          </div>

          <div className="form-section">
            <h3>Выбор места</h3>
            
            <div className="seat-selection">
              <div className="selected-seat-info">
                {selectedSeat ? (
                  <span>Выбрано место: <strong>{selectedSeat}</strong></span>
                ) : (
                  <span className="hint">Выберите место на схеме</span>
                )}
              </div>
              
              <div className="bus-layout">
                <div className="bus-driver">Водитель</div>
                <div className="bus-door">Дверь</div>
                
                {busLayout.map((row, rowIndex) => (
                  <div key={rowIndex} className="bus-row">
                    {row.map((seat, seatIndex) => (
                      seat === null ? (
                        <div key={`${rowIndex}-${seatIndex}`} className="aisle"></div>
                      ) : (
                        <button
                          key={seat}
                          type="button"
                          className={`seat ${selectedSeat === seat ? 'selected' : ''}`}
                          onClick={() => handleSeatClick(seat)}
                        >
                          {seat}
                        </button>
                      )
                    ))}
                  </div>
                ))}
              </div>
            </div>
          </div>

          <button type="submit" className="submit-btn" disabled={!selectedSeat}>
            Подтвердить бронирование
          </button>
        </form>
      </section>
    </div>
  );
};

export default Ticket;
