// src/components/SearchForm.js
import { useState } from 'react';
import '../styles/searchForm.css'; // Подключаем стили
import RouteList from "./RouteList";

export default function SearchForm({ onSearch }) {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');
  const [citySuggestions, setCitySuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  const cities = [
    "Москва", "Санкт-Петербург", "Новосибирск", "Екатеринбург", "Нижний Новгород", 
    "Казань", "Челябинск", "Омск", "Самара", "Ростов-на-Дону"
  ]; // Список городов для примера

  // Функция для фильтрации городов по введенному символу
  const handleCityInput = (e, cityType) => {
    const query = e.target.value;
    if (cityType === 'from') {
      setFrom(query);
    } else {
      setTo(query);
    }

    if (query.length > 1) {
      const filteredCities = cities.filter(city =>
        city.toLowerCase().includes(query.toLowerCase())
      );
      setCitySuggestions(filteredCities);
      setShowSuggestions(true);
    } else {
      setShowSuggestions(false);
    }
  };

  const handleCitySelect = (city, cityType) => {
    if (cityType === 'from') {
      setFrom(city);
    } else {
      setTo(city);
    }
    setShowSuggestions(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch({ from, to, date });
  };

  return (
    <form onSubmit={handleSubmit} className="search-form">
      <input
        value={from}
        onChange={(e) => handleCityInput(e, 'from')}
        placeholder="Откуда"
        required
      />
      {showSuggestions && from && (
        <ul className="city-suggestions">
          {citySuggestions.map((city, index) => (
            <li key={index} onClick={() => handleCitySelect(city, 'from')}>{city}</li>
          ))}
        </ul>
      )}

      <input
        value={to}
        onChange={(e) => handleCityInput(e, 'to')}
        placeholder="Куда"
        required
      />
      {showSuggestions && to && (
        <ul className="city-suggestions">
          {citySuggestions.map((city, index) => (
            <li key={index} onClick={() => handleCitySelect(city, 'to')}>{city}</li>
          ))}
        </ul>
      )}

      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        required
      />
      <button type="submit">Найти</button>
    </form>
  );
}
