import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Понадобится axios
import '../../styles/admin.css'; // Импорт стилей

export default function AdminPanel() {
  const navigate = useNavigate();
  const [routes, setRoutes] = useState([]);
  const [newRoute, setNewRoute] = useState({
    from: '',
    to: '',
    date: '',
    price: '',
  });

  const handleLogout = () => {
    localStorage.removeItem('isAdmin');
    localStorage.removeItem('token');
    navigate('/admin/login');
  };

  useEffect(() => {
    const isAdmin = localStorage.getItem('isAdmin') === 'true';
    if (!isAdmin) navigate('/admin/login');
    else fetchRoutes(); // загрузить маршруты при старте
  }, [navigate]);

  const fetchRoutes = async () => {
    try {
        const response = await axios.get('/api/routes');
      setRoutes(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке маршрутов:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRoute((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddRoute = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token'); // Получаем токен из localStorage
      const response = await axios.post('http://localhost:5000/admin/routes', newRoute, {
        headers: {
          Authorization: `Bearer ${token}`, // Отправляем токен в заголовках
        },
      });
      setRoutes((prevRoutes) => [...prevRoutes, response.data]); // Добавляем новый маршрут
      setNewRoute({ from: '', to: '', date: '', price: '' });
    } catch (error) {
      console.error('Ошибка при добавлении маршрута:', error.response ? error.response.data : error.message);
    }
  };
  

  const handleDeleteRoute = async (id) => {
    try {
      await axios.delete(`/api/routes/${id}`);
      setRoutes((prevRoutes) => prevRoutes.filter((route) => route._id !== id));
    } catch (error) {
      console.error('Ошибка при удалении маршрута:', error);
    }
  };

  return (
    <div className="admin-container">
      <header className="admin-header">
        <h1 className="admin-title">Панель администратора</h1>
        <button className="logout-btn" onClick={handleLogout}>
          Выйти
        </button>
      </header>
      
      <div className="admin-content">
        <h2>Управление маршрутами</h2>

        {/* Форма добавления маршрута */}
        <form onSubmit={handleAddRoute} className="add-route-form">
          <input
            type="text"
            name="from"
            placeholder="Откуда"
            value={newRoute.from}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="to"
            placeholder="Куда"
            value={newRoute.to}
            onChange={handleInputChange}
          />
          <input
            type="date"
            name="date"
            value={newRoute.date}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="price"
            placeholder="Цена"
            value={newRoute.price}
            onChange={handleInputChange}
          />
          <button type="submit" className="add-btn">Добавить рейс</button>
        </form>

        {/* Таблица маршрутов */}
        <table className="routes-table">
          <thead>
            <tr>
              <th>Откуда</th>
              <th>Куда</th>
              <th>Дата</th>
              <th>Цена</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {routes.map((route) => (
              <tr key={route._id}>
                <td>{route.from}</td>
                <td>{route.to}</td>
                <td>{route.date}</td>
                <td>{route.price} ₽</td>
                <td>
                  <button className="delete-btn" onClick={() => handleDeleteRoute(route._id)}>Удалить</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
