import React, { useState, useContext, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import logo from '../logo.svg';
import '../styles/header.css';
import Login from './Login';
import { AuthContext } from '../context/AuthContext';
import Register from './Register';
import { FaUser, FaCog, FaSignOutAlt, FaBell, FaSearch } from 'react-icons/fa';
import { RiMenu3Line, RiCloseLine } from 'react-icons/ri';

const Header = () => {
  const [isLoginOpen, setLoginOpen] = useState(false);
  const [isRegisterOpen, setRegisterOpen] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const { user, isAuth, logout } = useContext(AuthContext);
  const menuRef = useRef();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header className="header">
      <div className="container">
        <div className="header-left">
          <Link to="/" className="logo">
            <img src={logo} alt="Логотип" />
            <span>Travely</span>
          </Link>

          <nav className={`nav ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
            <Link to="/">Главная</Link>
            <Link to="/tours">Путевки</Link>
            <Link to="/destinations">Направления</Link>
            <Link to="/support">Поддержка</Link>

            <div className="search-mobile">
              <FaSearch className="search-icon" />
              <input type="text" placeholder="Поиск путешествий..." />
            </div>
          </nav>
        </div>

        <div className="header-right">
          <div className="search-desktop">
            <FaSearch className="search-icon" />
            <input type="text" placeholder="Поиск..." />
          </div>

          {isAuth ? (
            <div className="auth-actions">
              <button className="notification-btn">
                <FaBell />
                <span className="notification-badge">3</span>
              </button>

              <div className="user-menu-container" ref={menuRef}>
                <div 
                  className="user-avatar" 
                  onClick={() => setIsMenuOpen(!isMenuOpen)}
                >
                  <img 
                    src={user?.avatar || `https://ui-avatars.com/api/?name=${user?.email[0]}&background=random`} 
                    alt="Аватар" 
                    onError={(e) => {
                      e.target.src = `https://ui-avatars.com/api/?name=U&background=random`;
                    }}
                  />
                </div>

                {isMenuOpen && (
                  <div className="dropdown-menu">
                    <div className="user-info">
                      <img src={user?.avatar || `https://ui-avatars.com/api/?name=${user?.email[0]}&background=random`} alt="Аватар" />
                      <div>
                        <h4>{user?.name || user?.email}</h4>
                        <p>{user?.email}</p>
                      </div>
                    </div>
                    <Link to="/profile" onClick={() => setIsMenuOpen(false)}>
                      <FaUser className="menu-icon" /> Профиль
                    </Link>
                    <Link to="/settings" onClick={() => setIsMenuOpen(false)}>
                      <FaCog className="menu-icon" /> Настройки
                    </Link>
                    <button onClick={() => { logout(); setIsMenuOpen(false); }}>
                      <FaSignOutAlt className="menu-icon" /> Выйти
                    </button>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="auth-buttons">
              <button 
                className="login-btn" 
                onClick={() => setLoginOpen(true)}
              >
                Войти
              </button>
              <button 
                className="register-btn" 
                onClick={() => setRegisterOpen(true)}
              >
                Регистрация
              </button>
            </div>
          )}

          <button 
            className="mobile-menu-toggle" 
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            {isMobileMenuOpen ? <RiCloseLine /> : <RiMenu3Line />}
          </button>
        </div>
      </div>

      {/* Модальные окна */}
      {isLoginOpen && (
        <Login
          onClose={() => setLoginOpen(false)}
          onSwitchToRegister={() => {
            setLoginOpen(false);
            setRegisterOpen(true);
          }}
        />
      )}

      {isRegisterOpen && (
        <Register
          onClose={() => setRegisterOpen(false)}
          onSwitchToLogin={() => {
            setRegisterOpen(false);
            setLoginOpen(true);
          }}
        />
      )}
    </header>
  );
};

export default Header;
