import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ticket.css'

const Ticket = () => {
  const { routeId } = useParams();  // Получаем routeId из URL
  const navigate = useNavigate();
  const [route, setRoute] = useState(null);
  const [seats, setSeats] = useState([]);  // Это может быть список мест, которые ты получишь с сервера
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [passengerInfo, setPassengerInfo] = useState({ name: '', passport: '' });

useEffect(() => {
  // 1. Проверка routeId
  if (!routeId || typeof routeId !== 'string') {
    console.error('Invalid routeId:', routeId);
    return;
  }

  // 2. Асинхронная функция внутри эффекта
  const fetchRouteDetails = async () => {
    try {
      // 3. Жёсткий дебаг URL
      const apiUrl = `http://localhost:5000/api/routes/${routeId}`;
      console.log('Fetching URL:', apiUrl); // Проверяем в консоли!

      // 4. Запрос с явными заголовками
      const response = await fetch(apiUrl, {
        headers: {
          'Accept': 'application/json',
          'Cache-Control': 'no-cache'
        }
      });

      const routeData = await response.json();
      setRoute(routeData);

    } catch (error) {
      console.log(error);
    }
  };

  fetchRouteDetails();

}, [routeId]);

  // Обработка выбора места
  const handleSeatSelect = (seat) => {
    setSelectedSeat(seat);
  };

  // Обработка изменения данных пассажира
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPassengerInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Обработка отправки данных билета
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedSeat) {
      alert('Выберите место');
      return;
    }

    try {
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          routeId,
          seatNumber: selectedSeat,
          passengerName: passengerInfo.name,
          passport: passengerInfo.passport,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        navigate(`/booking-confirmation/${data._id}`);
      } else {
        alert(data.message || 'Ошибка при бронировании');
      }
    } catch (err) {
      console.error('Ошибка бронирования:', err);
      alert('Произошла ошибка при отправке данных');
    }
  };

return (
    <div className="ticket-booking">
      <header className="booking-header">
        <h1>Оформление билета</h1>
        <div className="route-badge">№ {routeId}</div>
      </header>

      {route && (
        <section className="route-details">
          <div className="route-info">
            <div className="info-block">
              <span className="info-label">Направление</span>
              <h2>{route.from} → {route.to}</h2>
            </div>
            
            <div className="info-grid">
              <div className="info-item">
                <span className="info-label">Дата отправления</span>
                <p>{new Date(route.date).toLocaleDateString()}</p>
              </div>
              <div className="info-item">
                <span className="info-label">Время</span>
                <p>{new Date(route.date).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}</p>
              </div>
              <div className="info-item">
                <span className="info-label">Цена</span>
                <p className="price">{route.price} ₽</p>
              </div>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="booking-form">
            <div className="form-section">
              <h3>Данные пассажира</h3>
              
              <div className="form-group">
                <label htmlFor="name">ФИО</label>
                <input
                  id="name"
                  type="text"
                  name="name"
                  value={passengerInfo.name}
                  onChange={(e) => setPassengerInfo({...passengerInfo, name: e.target.value})}
                  required
                  placeholder="Иванов Иван Иванович"
                />
              </div>

              <div className="form-group">
                <label htmlFor="passport">Паспортные данные</label>
                <input
                  id="passport"
                  type="text"
                  name="passport"
                  value={passengerInfo.passport}
                  onChange={(e) => setPassengerInfo({...passengerInfo, passport: e.target.value})}
                  required
                  placeholder="1234 567890"
                />
              </div>
            </div>

            <div className="form-section">
              <h3>Выбор места</h3>
              
              <div className="seat-selection">
                <label htmlFor="seat">Место в транспорте</label>
                <select
                  id="seat"
                  value={selectedSeat}
                  onChange={(e) => setSelectedSeat(e.target.value)}
                  required
                >
                  <option value="">Выберите место</option>
                  {seats.map(seat => (
                    <option key={seat} value={seat}>Место {seat}</option>
                  ))}
                </select>
                
                <div className="seat-map">
                  {/* Здесь можно добавить схему мест */}
                </div>
              </div>
            </div>

            <button type="submit" className="submit-btn">
              Подтвердить бронирование
            </button>
          </form>
        </section>
      )}
    </div>
  );
};

export default Ticket;
