import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './BookingConfirmation.css';

const BookingConfirmation = () => {
  const { ticketId } = useParams();
  const navigate = useNavigate();
  const [ticket, setTicket] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Начало загрузки билета, ID:', ticketId); // Логируем ID
    
    const fetchTicket = async () => {
      try {
        console.log('Отправка запроса на сервер...');
        const response = await fetch(`http://localhost:5000/api/tickets/${ticketId}`);
        
        console.log('Ответ сервера:', {
          status: response.status,
          ok: response.ok
        });

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(errorText || 'Билет не найден');
        }
        
        const data = await response.json();
        console.log('Полученные данные:', data); // Логируем полученные данные
        setTicket(data);
      } catch (err) {
        console.error('Ошибка при загрузке:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchTicket();
  }, [ticketId]);

  // Временный простой рендер для диагностики
  if (loading) return <div style={{padding: '20px'}}>Загрузка данных билета {ticketId}...</div>;
  if (error) return <div style={{padding: '20px', color: 'red'}}>Ошибка: {error}</div>;
  if (!ticket) return <div style={{padding: '20px'}}>Билет не найден</div>;

  return (
    <div style={{padding: '20px'}}>
      <h1>Тестовая страница</h1>
      <pre>{JSON.stringify(ticket, null, 2)}</pre>
    </div>
  );
};

export default BookingConfirmation;
