// components/RouteList.js
import React from 'react';
import '../styles/routeList.css';

export default function RouteList({ routes }) {
  return (
    <div className="route-list-container">
      <h2>Доступные маршруты</h2>

      {routes.length === 0 ? (
        <div className="route-empty">Маршруты не найдены</div>
      ) : (
        <table className="route-table">
          <thead>
            <tr>
              <th>Откуда</th>
              <th>Куда</th>
              <th>Дата</th>
              <th>Цена</th>
            </tr>
          </thead>
          <tbody>
            {routes.map((route) => (
              <tr key={route._id}>
                <td data-label="Откуда">{route.from}</td>
                <td data-label="Куда">{route.to}</td>
                <td data-label="Дата">{new Date(route.date).toLocaleDateString()}</td>
                <td data-label="Цена">{route.price} ₽</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
