import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/admin.css';

export default function AdminLogin() {
  const [email, setEmail] = useState('admin@example.com');
  const [password, setPassword] = useState('admin12345');
  const [error, setError] = useState(''); // Для хранения ошибки
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(''); // Сброс ошибки перед новой попыткой

    try {
      const res = await fetch('http://localhost:5000/api/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.message || 'Ошибка входа');
      }

      const data = await res.json();
      localStorage.setItem('token', data.token); // сохраняем JWT

      // После успешного входа проверяем токен
      const token = localStorage.getItem('token');
      if (token) {
        navigate('/admin');
      } else {
        setError('Не удалось получить токен, попробуйте снова.');
      }
    } catch (err) {
      setError(err.message || 'Неизвестная ошибка');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '50px auto' }}>
      <h1>Вход в админку</h1>
      
      {error && <div className="error">{error}</div>} {/* Показываем ошибку, если она есть */}

      <form onSubmit={handleLogin}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Пароль"
          required
        />
        <button type="submit">Войти</button>
      </form>
    </div>
  );
}
